'use strict';

var valid = [];
var test;

test = {
	'code': [
		'var arr = [{',
		'  key1: "value1",',
		'  key2: "value2"',
		'}];'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'function sum( x ) {',
		'  var sum = 0;',
		'  for ( var i = 0; i < x.length; i++ ) {',
		'    sum += x[ i ];',
		'  }',
		'  return sum;',
		'}',
		'var arrSum = sum([',
		'  1,',
		'  2,',
		'  3,',
		'  4',
		']);'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'function log( x ) {',
		'  for ( var i = 0; i < x.length; i++ ) {',
		'    console.log( x[ i ] );',
		'  }',
		'}',
		'log([{',
		'  a: 1,',
		'  b: 2',
		'}]);'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'console.log([',
		'  Math.random(),',
		'  Math.random()',
		']);',
		'/* e.g., =>',
		'  [',
		'    0.2580887012988746,',
		'    0.128454513229588',
		'  ]',
		'*/'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'console.log({',
		'  a: Math.random(),',
		'  b: Math.random()',
		'});'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'  var v = new ndindex({ // eslint-disable-line no-unused-vars',
		'    \'kind\': \'linear\'',
		'  });'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'  var v = new ndindex({',
		'    \'kind\': \'linear\'',
		'  });'
	].join( '\n' )
};
valid.push( test );


// EXPORTS //

module.exports = valid;
